project25 = Repo.insert!(%Project{class: "I&BD", type: 1, isCS: false, link: "http://www.w3schools.com", name: "Collectible Orchestral Era Tokens", class_short: "I&BD", semester: "Spring '21", desc: "A series of posters advertising rare, collectible tokens connected to various orchestral music eras. The center graphics of each poster/era were inspired the musical styles from each era. The Baroque era marked the beginning of light, modern structure in music compositions, the Classical era was defined by large, organized orchestral play for royalty and those in power, and the Romantic era was defined by compositions that were very emotional, expressive, and humanistic. The final set of posters were overlayed with a “Collect” tag and token shapes to further explain the product.", imgs: ["IBD/cover3.png", "IBD/3_2.png", "IBD/3_3.png", "IBD/3_4.png", "IBD/3_5.png"], tags: ["Graphic Design", "Illustrator"]});
project11 = Repo.insert!(%Project{class: "ID1", type: 1, isCS: false, link: "http://www.w3schools.com", name: "Narco-culture Infographic", class_short: "ID1", semester: "Fall '18", desc: "An infographic briefly detailing the history of Narcoculture in Mexico, highlighting the presence and distribution of various drug cartels that took over Mexico from the 1980’s to present day (2020).", imgs: ["ID1/cover1.png", "ID1/1_0.png", "ID1/1_1.png", "ID1/1_2.png", "ID1/1_3.png"], tags: ["Illustrator", "Information Design"]});
