project1 = Repo.insert!(%Project{class: "Personal", type: 2, isCS: false, link: "http://www.w3schools.com", name: "Portfolio Design Process", class_short: "Exploration", semester: "Summer '21", desc: "Documenting the process of how I built this portfolio. Taking from what I learned from a Web Development class, I further expanded on my front-end skills by working closely with React.js, Styled Components, and FramerAnimation to deliver an engaging experience for venturing through the various works I have worked on over my college career. I went through many different design iterations, taking from tons of sites I studied found on Awwwards and Httpster. This was a fun but difficult experience trying to pinpoint a specific website style and interaction flow.", imgs: ["Website/cover.png", "Website/1_1.png", "Website/1_2.png", "Website/1_3.png", "Website/1_4.png", "Website/1_5.mp4",], tags: ["React.js", "Elixir-Phoenix", "Figma", "Experience Design"]});
project23 = Repo.insert!(%Project{class: "I&BD", type: 1, isCS: false, link: "http://www.w3schools.com", name: "Personal Identity Exploration", class_short: "I&BD", semester: "Spring '21", desc: "A series of three self-promotional posters. I explored which traits are core to my identity, and what differentiates me from my peers. Intended to express my identity in a way that creates emotional resonance in a memorable, yet authentic manner. Picking and choosing between different graphical themes proved challenging yet fun and helped me to understand my values and preferences as a designer even further.", imgs: ["IBD/cover1.png", "IBD/1_0.png", "IBD/1_1.png", "IBD/1_2.png", "IBD/1_3.png", "IBD/1_4.png", "IBD/1_5.png", "IBD/1_6.png"], tags: ["Graphic Design", "Adobe Illustrator"]});
project10 = Repo.insert!(%Project{class: "GD1", type: 1, isCS: false, link: "http://www.w3schools.com", name: "International Holocaust Film Festival", class_short: "GD1", semester: "Fall '18", desc: "Developed a series of Stamps and Posters for the International Holocaust Film Festival. Inspired by, and pictures taken from the Holocaust Museum in downtown Boston", imgs: ["GD1/cover.png", "GD1/0.png", "GD1/1.png", " The posters were many to emphasize the importance of not forgetting those affected and lost by the holocaust, the stamps highlighting the countries most directly affected by concentration camps. The stamps and logo for the poster were a result of a 100-iteration design practice.", "GD1/2.png", "GD1/3.png", "GD1/4.png", "GD1/5.png", "GD1/6.png", "GD1/7.png"], tags: ["Illustrator", "Photography"]});
project24 = Repo.insert!(%Project{class: "I&BD", type: 1, isCS: false, link: "http://www.w3schools.com", name: "Album Trading Cards", class_short: "I&BD", semester: "Spring '21", desc: "A series of three marketing posters advertising an Album Trading Card Product. Inspired by the resurgence in value of pokemon trading cards, as well as the various amount of products that spotify has released over the years, I started this idea off with the intention of only targeting the hip-hop market, but expanded upon this product by designing different sets of cards, based on era and genre. I would have liked to do more “limited edition” sets of these cards, and packaging designs, but for this assignment I only explored a single set of posters.", imgs: ["IBD/cover2.png", "IBD/2_1.png", "IBD/2_2.png", "IBD/2_3.png", "IBD/2_4.png", "IBD/2_5.png", "IBD/2_6.png"], tags: ["Graphic Design", "Illustrator"]});
project18 = Repo.insert!(%Project{class: "GD2", type: 1, isCS: false, link: "http://www.w3schools.com", name: "Data Privacy Campaign", class_short: "GD2", semester: "Fall '19", desc: "A Data Privacy Campaign is a project aimed at bringing awareness to data privacy, and the dangers surrounding it. After the first iteration of designs, which emphasized how much data is kept and given to large data corporations, I pivoted to a more “Big-Brother” inspired design-style, saying that social media corporations are always watching, always collecting data on user’s habits. The final deliverable was a website design mockup, series of Instagram Advertisements, series of posters, laptop and phone stickers, and business cards.", imgs: ["GD2/cover.png", "GD2/1_2.jpg", "GD2/1_3.jpg", "GD2/1_4.jpg", "GD2/1_10.jpg", "GD2/1_9.png", "GD2/1_6.png", "GD2/1_7.png", "GD2/1_8.png", "GD2/1_11.jpg", "GD2/1_12.jpg", "GD2/1_5.jpg"], tags: ["Case Study", "InDesign", "Illustrator", "Graphic Design"]});
project17 = Repo.insert!(%Project{class: "Typography 2", type: 1, isCS: false, link: "http://www.w3schools.com", name: "88Rising Magazine", class_short: "Type 2", semester: "Spring '19", desc: "A series of three magazine styles covering the rise in popularity around the media group: 88Rising. Featuring artists such as Rich Brain, Niki, and the Higher Brothers, I ventured into magazine/book designing for the first time in hopes of creating layouts that were engaging and reflect the energetic youth of these groups.", imgs: ["Type2/cover3.png", "Type2/3_0.jpg", "Type2/3_1.png", "Type2/3_2.jpg", "Type2/3_3.png", "Type2/3_4.png", "Type2/3_5.png", "Type2/3_6.png"], tags: ["InDesign", "Typography", "Illustrator"]});
# project6 = Repo.insert!(%Project{class: "Type 1", type: 1, isCS: false, link: "http://www.w3schools.com", name: "Type Studies", class_short: "Type 1", semester: "Spring '18", desc: "A series of typography exploration exercises from a Typography 1 course. I explored analyzing and respecting the beauty of type by pinpointing and framing the various anatomy of type in one exercise, showcased a series of typefaces of my liking in another, and created a series of logos using only letterforms in another.", imgs: ["Type1/cover2.png", "Type1/2_0.jpg", "Type1/2_1.png", "Type1/2_2.jpg", "Type1/2_3.png", "Type1/2_4.jpg", "Type1/2_5.png", "Type1/2_6.png", "Type1/2_7.jpg"], tags: ["InDesign", "Typography"]});
# project20 = Repo.insert!(%Project{class: "ID: Mobile", type: 3, isCS: false, link: "http://www.w3schools.com", name: "PhotoWalk", class_short: "ID:M", semester: "Fall '20", desc: "Photowalk is an app designed to encourage both digital and film photographers to become more familiar with their cities by going on “Photo Walks”, and expanding on their photography skills. By being able to share experiences through photos, walk routes, and specific stop locations, photographers can help others newer to the city to find interesting streets to take their own pictures of.", imgs: ["IDM/cover1.png", "IDM/0.png", "IDM/1_0.png", "Some early sketches and journey map.", "IDM/1_1.png", "IDM/1_2.png", "First set of prototypes.", "IDM/1_3.png", "Final Prototype.", "IDM/1_4.png", "IDM/1_5.png", "IDM/1_6.png"], tags: ["Figma", "Mobile Design", "Experience Design"]});

